sys_script_include.script:
var GetSameDateLunchData = Class.create();
GetSameDateLunchData.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

recordExists: function() {
  var dateParam = this.getParameter('sysparm_date') || this.date;
  var userParam = this.getParameter('sysparm_user_id') || this.user;

  var gr = new GlideRecord('x_1732764_lunch_an_lunch_data');
  gr.addQuery('date', dateParam);
  gr.addQuery('created_by_user', userParam);
  gr.query();

  // booleanではなく文字列で返す
  return gr.hasNext() ? 'true' : 'false';
},

  // サーバーサイドから呼び出すための setter
  setParameter: function(name, value) {
    this[name] = value;
  },

  type: 'GetSameDateLunchData'
});
